mapper.select:
select bank_id
from sys_bank
where RECEIVING_PAYMENT_BANK = #{RECEIVING_PAYMENT_BANK}

select a.RECEIVING_BANK_NAME, a.RECEIVING_PAYMENT_BANK, a.RECEIVING_BANK_BRANCH, a.RECEIVING_NAME, a.CREATE_TIME, a.UPDATE_TIME, a.STATUS, a.bank_id from sys_bank a
and a.RECEIVING_BANK_NAME like concat ('%',#{pd.field1},'%')
and a.RECEIVING_PAYMENT_BANK like concat ('%',#{pd.field3},'%')
AND a.CREATE_TIME BETWEEN #{pd.lastLoginStart} AND #{pd.lastLoginEnd}
AND a.CREATE_TIME > #{pd.lastLoginStart}
AND a.CREATE_TIME < #{pd.lastLoginEnd}
and a.STATUS = #{pd.field2}
